import { useState, useLayoutEffect } from 'react';
import './App.css';

function App() {

  const [attendees, setAttendees] = useState([])


  useLayoutEffect(() => {
    ;
    (function getAttendees(){
      fetch(`http://localhost:5000/get_all`,  {
        method: 'GET',
        mode: "cors",
        headers: {
          Accept: 'application/json',
        }}) // fetch data từ api ve
      .then(response => response.json())
      .then(data =>{ 
        setAttendees(data)
    })
    })()
  }, [])

  return (
    <div className="App">
      <h3>
        Attendees's Information
      </h3>

      <div>
        {
          attendees.map((attendee) => (
            <div>
              <ul key={attendee._id}>
                <li>STT: {attendee.stt}</li>
                <li>Ho va ten: {attendee.name}</li>
                <li>Nam sinh: {attendee.yob}</li>
                <li>Truong: {attendee.school}</li>
                <li>Chuyen nganh: {attendee.major}</li>
              </ul>
              <br></br>
            </div>
          ))
        }
      </div>
    </div>
  );
}

export default App;
